mapper.select:
select team 
from users
where id=#{id}

mapper.update:
UPDATE matching
SET successmatching='true'
WHERE awayTeam=#{awayteam}

mapper.select:
select homepoint,awaypoint
from matching
where hometeam=#{homeTeam} and awayteam=#{awayTeam}

select homepoint,awaypoint
from matching
where hometeam=#{awayTeam} and awayteam=#{homeTeam}

select homepoint,awaypoint
from matching
where num=#{num}
