mapper.select:
select name,member,win,lose,total,ground
from team
where name=#{name}

select joinid
from joinlist
where jointeam=#{name}

mapper.update:
UPDATE users4
SET team=#{jointeam},responsibility='player'
WHERE id=#{joinid}

mapper.select:
select id,responsibility,b_position,career 
from users4
where team=#{name}

mapper.update:
update users4
set team=#{name},responsibility='leader'
where id=#{member}

mapper.select:
select awayTeam, matchDate, ground, successMatching, homeTeam
from matching
where homeTeam=#{homeTeam}

select successMatching, homeTeam
from matching
where homeTeam=#{homeTeam} and awayTeam=#{awayTeam}

select id, name ,career ,b_position
from users4
where id=#{joinid}

select name,win,lose,total,ground,leader
from team
where name=#{awayteam}

select jointeam
from joinlist
where joinid=#{id}
